import{_ as i,c as t,I as a,ac as s,l as e,a as n,D as o,o as l}from"./chunks/framework.C8XGpFQ0.js";const S=JSON.parse('{"title":"Einführung","description":"","frontmatter":{},"headers":[],"relativePath":"de/user/Introduction.md","filePath":"de/user/Introduction.md","lastUpdated":1712937334000}'),h={name:"de/user/Introduction.md"},c=s('<h1 id="einfuhrung" tabindex="-1">Einführung <a class="header-anchor" href="#einfuhrung" aria-label="Permalink to &quot;Einführung&quot;">​</a></h1><p>👋🏽 Willkommen bei Hyperion! Wir, das Hyperion Project Team freuen uns sehr dich hier zu sehen! Wenn du Hyperion noch nicht kennst, lass es uns kurz vorstellen.</p><h2 id="💡-was-ist-hyperion" tabindex="-1">💡 Was ist Hyperion? <a class="header-anchor" href="#💡-was-ist-hyperion" aria-label="Permalink to &quot;💡 Was ist Hyperion?&quot;">​</a></h2><p><a href="https://github.com/hyperion-project/hyperion.ng" target="_blank" rel="noreferrer">Hyperion</a> ist eine <strong>freie und quelloffene</strong> (unter der MIT Lizenz) Ambilight Software-Implementierung für <strong>GNU/Linux</strong>, <strong>macOS</strong> und <strong>Windows</strong>, die du vielleicht von einem nicht weiter genannten TV-Hersteller kennst. Es unterstützt eine Vielzahl von verschiedenen LED-Geräten und Video Erfassungsmethoden.</p><p>In einfachen Worten, unsere Software verarbeitet Video Eingangssignale und wandelt diese Signal in Umgebungslicht um.</p><h2 id="🔥-eigenschaften" tabindex="-1">🔥 Eigenschaften <a class="header-anchor" href="#🔥-eigenschaften" aria-label="Permalink to &quot;🔥 Eigenschaften&quot;">​</a></h2><ul><li>Geringe Prozessor Last, ideal für SoCs wie Raspberry Pi</li><li>JSON-Schnittstelle (API) für einfache Integration in Skripte oder andere Anwendungen</li><li>Kommandozeilenprogramm zum Testen und zur Integration in eine automatisierte Umgebung</li><li>Prioritätskanäle sind nicht an einen bestimmten Anbieter von LED-Daten gebunden, d.h. ein Anbieter kann LED-Daten hinzufügen und entfernen, ohne eine Verbindung zu Hyperion aufrechterhalten zu müssen.</li><li>Erkennung von schwarzen Balken</li><li>Eine Effekt-Engine mit vielen eingebauten Effekten, die zusätzlich eigene Effekte über Python Skripte erlaubt</li><li>Ein mehrsprachiges Web-Interface zur Konfiguration und Steuerung von Hyperion</li></ul><h2 id="💬-sprachen" tabindex="-1">💬 Sprachen <a class="header-anchor" href="#💬-sprachen" aria-label="Permalink to &quot;💬 Sprachen&quot;">​</a></h2><ul><li><strong>Verfügbar in mehr als 25 verschiedenen Sprachen</strong> wie Englisch, Deutsch, Französisch, Polnisch, Chinesisch, Japanisch, Russisch, Portugiesisch, Ungarisch, Niederländisch und Spanisch</li><li><a href="https://poeditor.com/join/project/Y4F6vHRFjA" target="_blank" rel="noreferrer">Deine Hilfe</a> können wir immer gebrauchen, um Übersetzungen zu verbessern oder Hyperion in weitere Sprachen zu übersetzen</li></ul><h2 id="🧑‍💻-mitwirkende" tabindex="-1">🧑‍💻 Mitwirkende <a class="header-anchor" href="#🧑‍💻-mitwirkende" aria-label="Permalink to &quot;🧑‍💻 Mitwirkende&quot;">​</a></h2>',10),u=e("p",null,"Dieses Projekt existiert dank all der Menschen, die dazu beitragen.",-1),d=e("h2",{id:"📝-lizenz",tabindex:"-1"},[n("📝 Lizenz "),e("a",{class:"header-anchor",href:"#📝-lizenz","aria-label":'Permalink to "📝 Lizenz"'},"​")],-1),g=e("p",null,[n("Der Quellcode ist unter der MIT-Lizenz veröffentlicht (see "),e("a",{href:"https://opensource.org/licenses/MIT",target:"_blank",rel:"noreferrer"},"https://opensource.org/licenses/MIT"),n(").")],-1);function p(f,m,b,_,z,k){const r=o("Contributors");return l(),t("div",null,[c,a(r),u,d,g])}const P=i(h,[["render",p]]);export{S as __pageData,P as default};
